import React, { useEffect, useState } from 'react';
import axios from 'axios';

const TaskList = () => {
  const [tasks, setTasks] = useState([]);

  // Fetch tasks from the backend
  const fetchTasks = async () => {
    try {
      const res = await axios.get('http://localhost:5000/api/tasks');
      setTasks(res.data);
    } catch (err) {
      console.error('Error fetching tasks:', err);
    }
  };

  // Delete a task
  const handleDelete = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/api/tasks/${id}`);
      setTasks(tasks.filter(task => task._id !== id));
    } catch (err) {
      console.error('Error deleting task:', err);
    }
  };

  // Toggle task completion status
  const toggleCompletion = async (id, completed) => {
    try {
      const res = await axios.put(`http://localhost:5000/api/tasks/${id}`, {
        completed: !completed,
      });
      setTasks(tasks.map(task => task._id === id ? res.data : task));
    } catch (err) {
      console.error('Error updating task:', err);
    }
  };

  useEffect(() => {
    fetchTasks();
  }, []);

  return (
    <div className="max-w-4xl mx-auto mt-8 px-4">
      <h2 className="text-3xl font-semibold text-center mb-6">Task Manager</h2>
      {tasks.length === 0 ? (
        <p className="text-center text-xl">No tasks found.</p>
      ) : (
        <ul className="space-y-4">
          {tasks.map(task => (
            <li key={task._id} className="bg-white p-4 rounded-lg shadow-md">
              <div className="flex justify-between items-center">
                <div>
                  <strong className="text-lg">{task.title}</strong><br />
                  {task.description && <em className="text-sm text-gray-500">{task.description}</em>}<br />
                  <span className={`text-sm ${task.completed ? 'text-green-500' : 'text-red-500'}`}>
                    {task.completed ? '✅ Completed' : '❌ Incomplete'}
                  </span>
                </div>
                <div className="space-x-2">
                  <button
                    onClick={() => toggleCompletion(task._id, task.completed)}
                    className="px-4 py-2 bg-blue-500 text-white rounded hover:bg-blue-600 transition duration-300"
                  >
                    Mark as {task.completed ? 'Incomplete' : 'Completed'}
                  </button>
                  <button
                    onClick={() => handleDelete(task._id)}
                    className="px-4 py-2 bg-red-500 text-white rounded hover:bg-red-600 transition duration-300"
                  >
                    Delete
                  </button>
                </div>
              </div>
              <hr className="my-2 border-t-2 border-gray-200" />
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default TaskList;
