// src/pages/Admin.js
import React, { useEffect, useState } from 'react';
import { fetchTasks, deleteTask, updateTask } from '../services/api';
import { decodeJWT } from '../utils/jwtUtils';

const Admin = () => {
  const [tasks, setTasks] = useState([]);

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) return;

    const loadTasks = async () => {
      try {
        const res = await fetchTasks(token);
        setTasks(res.data);
      } catch (err) {
        console.error('Error fetching tasks:', err);
      }
    };

    loadTasks();
  }, []);

  const handleDelete = async (id) => {
    const token = localStorage.getItem('token');
    try {
      await deleteTask(id, token);
      setTasks(tasks.filter((task) => task._id !== id));
    } catch (err) {
      console.error('Error deleting task:', err);
    }
  };

  const handleToggleComplete = async (id) => {
    const token = localStorage.getItem('token');
    const task = tasks.find((t) => t._id === id);
    try {
      const res = await updateTask(id, { completed: !task.completed }, token);
      setTasks(tasks.map((t) => (t._id === id ? res.data : t)));
    } catch (err) {
      console.error('Error updating task:', err);
    }
  };

  return (
    <div className="min-h-screen bg-gray-100 p-6">
      <h2 className="text-3xl font-bold text-center mb-6">Welcome to the admin dashboard!</h2>
      {tasks.length === 0 ? (
        <p className="text-center text-gray-600">No tasks available.</p>
      ) : (
        <ul className="space-y-4 max-w-3xl mx-auto">
          {tasks.map((task) => (
            <li
              key={task._id}
              className="bg-white shadow p-4 rounded-lg flex justify-between items-center"
            >
              <div>
                <p className={`text-lg ${task.completed ? 'line-through text-green-600' : ''}`}>
                  {task.title}
                </p>
                <p className="text-sm text-gray-500">{task.description}</p>
              </div>
              <div className="flex space-x-2">
                <button
                  onClick={() => handleToggleComplete(task._id)}
                  className="px-3 py-1 bg-yellow-500 text-white rounded"
                >
                  {task.completed ? 'Mark Incomplete' : 'Mark Complete'}
                </button>
                <button
                  onClick={() => handleDelete(task._id)}
                  className="px-3 py-1 bg-red-500 text-white rounded"
                >
                  Delete
                </button>
              </div>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default Admin;
