// src/App.js
import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';

import Home from './pages/Home';
import Login from './pages/Login';
import Admin from './pages/Admin';
import { decodeJWT } from './utils/jwtUtils';

// Protected route for normal users
function ProtectedRoute({ children }) {
  const token = localStorage.getItem('token');
  const user = decodeJWT(token);

  if (!token || !user) {
    return <Navigate to="/login" replace />;
  }

  return children;
}

// Protected route for admins only
function ProtectedAdminRoute({ children }) {
  const token = localStorage.getItem('token');
  const user = decodeJWT(token);

  if (!token || !user || user.role !== 'admin') {
    return <Navigate to="/login" replace />;
  }

  return children;
}

// New component to redirect '/' based on user role
function RoleBasedRedirect() {
  const token = localStorage.getItem('token');
  const user = decodeJWT(token);

  if (!token || !user) {
    return <Navigate to="/login" replace />;
  }

  // Redirect admin to /admin, user to /home (or /)
  if (user.role === 'admin') {
    return <Navigate to="/admin" replace />;
  } else {
    return <Navigate to="/" replace />; // or '/home' if you have that route
  }
}

function App() {
  return (
    <Router>
      <Routes>
        {/* Root route now uses RoleBasedRedirect */}
        <Route path="/" element={<RoleBasedRedirect />} />

        <Route
          path="/home"
          element={
            <ProtectedRoute>
              <Home />
            </ProtectedRoute>
          }
        />
        <Route path="/login" element={<Login />} />
        <Route
          path="/admin"
          element={
            <ProtectedAdminRoute>
              <Admin />
            </ProtectedAdminRoute>
          }
        />
      </Routes>
    </Router>
  );
}

export default App;
