import React, { useEffect, useState } from 'react';
import axios from 'axios';

const TaskList = () => {
  const [tasks, setTasks] = useState([]);

  const fetchTasks = async () => {
    try {
      const res = await axios.get('http://localhost:5000/api/tasks');
      setTasks(res.data);
    } catch (err) {
      console.error('Error fetching tasks:', err);
    }
  };

  const handleDelete = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/api/tasks/${id}`);
      setTasks(tasks.filter(task => task._id !== id));
    } catch (err) {
      console.error('Error deleting task:', err);
    }
  };

  const toggleCompletion = async (id, completed) => {
    try {
      const res = await axios.put(`http://localhost:5000/api/tasks/${id}`, {
        completed: !completed,
      });
      setTasks(tasks.map(task => task._id === id ? res.data : task));
    } catch (err) {
      console.error('Error updating task:', err);
    }
  };

  useEffect(() => {
    fetchTasks();
  }, []);

  return (
    <div className="container mx-auto p-6">
      <h2 className="text-3xl font-semibold mb-6 text-center">Task Manager</h2>
      {tasks.length === 0 ? (
        <p className="text-gray-500 text-center">No tasks found.</p>
      ) : (
        <ul className="space-y-4">
          {tasks.map(task => (
            <li
              key={task._id}
              className="p-4 bg-white rounded-lg shadow-md border border-gray-200 hover:shadow-lg hover:bg-gray-50 transition cursor-pointer"
            >
              <div className="flex justify-between items-center">
                <div className="flex-1">
                  <strong className="text-lg">{task.title}</strong>
                  {task.description && <p className="text-gray-600 mt-1">{task.description}</p>}
                </div>
                <div className="flex items-center space-x-3">
                  <span className={`text-sm font-semibold ${task.completed ? 'text-green-600' : 'text-red-600'}`}>
                    {task.completed ? '✅ Completed' : '❌ Incomplete'}
                  </span>
                  <button
                    className="bg-blue-500 text-white px-3 py-1 rounded-lg hover:bg-blue-600 focus:outline-none focus:ring-2 focus:ring-blue-400 transition"
                    onClick={() => toggleCompletion(task._id, task.completed)}
                  >
                    Mark as {task.completed ? 'Incomplete' : 'Completed'}
                  </button>
                  <button
                    className="bg-red-500 text-white px-3 py-1 rounded-lg hover:bg-red-600 focus:outline-none focus:ring-2 focus:ring-red-400 transition"
                    onClick={() => handleDelete(task._id)}
                  >
                    Delete
                  </button>
                </div>
              </div>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default TaskList;
