import axios from 'axios';

const API = axios.create({
  baseURL: 'http://localhost:5000/api',
});

// Attach token to request headers for protected routes
const authConfig = (token) => ({
  headers: {
    Authorization: `Bearer ${token}`,
  },
});

// Public - no token needed
export const fetchTasks = () => API.get('/tasks');

// Protected - token needed
export const createTask = (task, token) => API.post('/tasks', task, authConfig(token));
export const deleteTask = (id, token) => API.delete(`/tasks/${id}`, authConfig(token));
export const updateTask = (id, updatedTask, token) => API.put(`/tasks/${id}`, updatedTask, authConfig(token));
