import React, { useEffect, useState } from 'react';
import axios from 'axios';

const TaskList = () => {
  const [tasks, setTasks] = useState([]);
  const [highlightedTaskId, setHighlightedTaskId] = useState(null);

  const fetchTasks = async () => {
    try {
      const res = await axios.get('http://localhost:5000/api/tasks');
      setTasks(res.data);
    } catch (err) {
      console.error('Error fetching tasks:', err);
    }
  };

  const handleDelete = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/api/tasks/${id}`);
      setTasks(tasks.filter(task => task._id !== id));
    } catch (err) {
      console.error('Error deleting task:', err);
    }
  };

  const toggleCompletion = async (id, completed) => {
    try {
      const res = await axios.put(`http://localhost:5000/api/tasks/${id}`, {
        completed: !completed,
      });
      setTasks(tasks.map(task => task._id === id ? res.data : task));

      // Highlight the updated task
      setHighlightedTaskId(id);

      // Remove highlight after 2 seconds
      setTimeout(() => setHighlightedTaskId(null), 2000);
    } catch (err) {
      console.error('Error updating task:', err);
    }
  };

  useEffect(() => {
    fetchTasks();
  }, []);

  return (
    <div className="container mx-auto p-6">
      <h2 className="text-3xl font-semibold mb-4">Task Manager</h2>
      {tasks.length === 0 ? (
        <p className="text-gray-500">No tasks found.</p>
      ) : (
        <ul className="space-y-4">
          {tasks.map(task => (
            <li
              key={task._id}
              className={`p-4 border rounded-lg shadow-md bg-white transition-colors duration-500
                ${highlightedTaskId === task._id ? 'bg-yellow-100 border-yellow-400' : ''}`}
            >
              <div className="flex justify-between">
                <div className="flex-1">
                  <strong className="text-lg">{task.title}</strong><br />
                  {task.description && <em className="text-gray-600">{task.description}</em>}
                </div>
                <div className="flex items-center space-x-2">
                  <span className={`text-sm ${task.completed ? 'text-green-500' : 'text-red-500'}`}>
                    {task.completed ? '✅ Completed' : '❌ Incomplete'}
                  </span>
                  <button
                    className="bg-blue-500 text-white px-4 py-2 rounded-lg hover:bg-blue-600 transition"
                    onClick={() => toggleCompletion(task._id, task.completed)}
                  >
                    Mark as {task.completed ? 'Incomplete' : 'Completed'}
                  </button>
                  <button
                    className="bg-red-500 text-white px-4 py-2 rounded-lg hover:bg-red-600 transition"
                    onClick={() => handleDelete(task._id)}
                  >
                    Delete
                  </button>
                </div>
              </div>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default TaskList;
