import React, { useEffect, useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate, useNavigate } from 'react-router-dom';

import { fetchTasks, createTask, deleteTask, updateTask } from './services/api';

import TaskList from './components/TaskList';
import TaskForm from './components/TaskForm';
import Login from './pages/Login';
import Admin from './pages/Admin';              // Admin component path
import LogoutButton from './components/LogoutButton';  // Import LogoutButton here because used in Home

// Utility to decode JWT payload
function decodeJWT(token) {
  try {
    const base64Url = token.split('.')[1];
    const base64 = base64Url.replace(/-/g, '+').replace(/_/g, '/');
    return JSON.parse(window.atob(base64));
  } catch {
    return null;
  }
}

// Home component - Protected route (only logged in users)
function Home() {
  const [tasks, setTasks] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) {
      navigate('/login');
      return;
    }
    getTasks();
  }, [navigate]);

  const getTasks = async () => {
    try {
      const res = await fetchTasks();
      setTasks(res.data);
    } catch (err) {
      console.error('Error fetching tasks:', err);
    }
  };

  const handleAddTask = async (task) => {
    try {
      const res = await createTask(task);
      setTasks([...tasks, res.data]);
    } catch (err) {
      console.error('Error adding task:', err);
    }
  };

  const handleDelete = async (id) => {
    try {
      await deleteTask(id);
      setTasks(tasks.filter(task => task._id !== id));
    } catch (err) {
      console.error('Error deleting task:', err);
    }
  };

  const handleUpdate = async (id, updatedTask) => {
    try {
      const res = await updateTask(id, updatedTask);
      setTasks(tasks.map(task => task._id === id ? res.data : task));
    } catch (err) {
      console.error('Error updating task:', err);
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-r from-blue-400 via-blue-600 to-blue-800 p-4">
      <div className="max-w-3xl mx-auto bg-white p-6 rounded-lg shadow-md relative">
        <LogoutButton />
        <h1 className="text-3xl font-bold text-center mb-6 text-black">📝 Task Manager</h1>
        <TaskForm onSubmit={handleAddTask} />
        <TaskList tasks={tasks} onDelete={handleDelete} onUpdate={handleUpdate} />
      </div>
    </div>
  );
}

// Protected route for Admin page
function ProtectedAdminRoute({ children }) {
  const token = localStorage.getItem('token');

  if (!token) {
    return <Navigate to="/login" replace />;
  }

  const user = decodeJWT(token);

  if (!user || user.role !== 'admin') {
    return <Navigate to="/" replace />;
  }

  return children;
}

// Main App with routes
function App() {
  return (
    <Router>
      <Routes>
        <Route path="/" element={<Home />} />
        <Route path="/login" element={<Login />} />
        <Route
          path="/admin"
          element={
            <ProtectedAdminRoute>
              <Admin />
            </ProtectedAdminRoute>
          }
        />
      </Routes>
    </Router>
  );
}

export default App;
