import React, { useEffect, useState } from 'react';
import { Routes, Route, Navigate, useNavigate } from 'react-router-dom';

import { fetchTasks, createTask, deleteTask, updateTask } from './services/api';

import TaskList from './components/TaskList';
import TaskForm from './components/TaskForm';
import Login from './pages/Login';
import Admin from './pages/Admin';
import LogoutButton from './components/LogoutButton';

// Utility function to decode JWT token payload safely
function decodeJWT(token) {
  try {
    const base64Url = token.split('.')[1];
    const base64 = base64Url.replace(/-/g, '+').replace(/_/g, '/');
    return JSON.parse(window.atob(base64));
  } catch {
    return null;
  }
}

// Home Component (Protected Route for logged-in users)
function Home() {
  const [tasks, setTasks] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) {
      navigate('/login');
      return;
    }
    getTasks(token);
  }, [navigate]);

  const getTasks = async (token) => {
    try {
      const res = await fetchTasks(token); // pass token for auth header
      setTasks(res.data);
    } catch (err) {
      console.error('Error fetching tasks:', err);
      if (err.response && err.response.status === 401) {
        // Token expired or invalid, logout user
        localStorage.removeItem('token');
        navigate('/login');
      }
    }
  };

  const handleAddTask = async (task) => {
    try {
      const token = localStorage.getItem('token');
      const res = await createTask(task, token);
      setTasks([...tasks, res.data]);
    } catch (err) {
      console.error('Error adding task:', err);
    }
  };

  const handleDelete = async (id) => {
    try {
      const token = localStorage.getItem('token');
      await deleteTask(id, token);
      setTasks(tasks.filter(task => task._id !== id));
    } catch (err) {
      console.error('Error deleting task:', err);
    }
  };

  const handleUpdate = async (id, updatedTask) => {
    try {
      const token = localStorage.getItem('token');
      const res = await updateTask(id, updatedTask, token);
      setTasks(tasks.map(task => (task._id === id ? res.data : task)));
    } catch (err) {
      console.error('Error updating task:', err);
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-r from-blue-400 via-blue-600 to-blue-800 p-4">
      <div className="max-w-3xl mx-auto bg-white p-6 rounded-lg shadow-md relative">
        <LogoutButton />
        <h1 className="text-3xl font-bold text-center mb-6 text-black">üìù Task Manager</h1>
        <TaskForm onSubmit={handleAddTask} />
        <TaskList tasks={tasks} onDelete={handleDelete} onUpdate={handleUpdate} />
      </div>
    </div>
  );
}

// Protected Admin Route wrapper
function ProtectedAdminRoute({ children }) {
  const token = localStorage.getItem('token');
  if (!token) {
    return <Navigate to="/login" replace />;
  }

  const user = decodeJWT(token);
  if (!user || user.role !== 'admin') {
    return <Navigate to="/" replace />;
  }

  return children;
}

// Main App with routing (Router is in index.js)
function App() {
  return (
    <Routes>
      <Route path="/" element={<Home />} />
      <Route path="/login" element={<Login />} />
      <Route
        path="/admin"
        element={
          <ProtectedAdminRoute>
            <Admin />
          </ProtectedAdminRoute>
        }
      />
      {/* Redirect any unknown routes */}
      <Route path="*" element={<Navigate to="/" replace />} />
    </Routes>
  );
}

export default App;
